import React, { useState } from "react";
import "./ConvertUrl.css";
import Axios from "axios";

function ConvertUrl() {
  const [urllist, setUrlList] = useState([]);
  const [items, setItems] = useState([]);

  const shortUrlList = () => {
    Axios.get("http://localhost:3001/urlrecord").then((response) => {
      setUrlList(response.data);
      localStorage.setItem("shortlinks", response.data);
    });

    const links = localStorage.getItem("shortlinks");

    setItems(urllist);

    console.log(items);
  };

  return (
    <div className="container">
      <h1>URL SHORTENER</h1>
      <div className="information">
        <form action="" className="row g-2">
          <div className="col-9">
            <label htmlFor="inputPassword2" className="visually-hidden">
              Password
            </label>
            <input
              type="text"
              className="form-control form-control-lg"
              id="inputPassword2"
              placeholder="Password"
            />
          </div>
          <div className="col-3">
            <button className="btn btn-primary btn-lg mb-3">Shorten</button>
          </div>
        </form>
        <div className="showurl">
          <button
            className="btn btn-primary btn-lg mb-3"
            onClick={shortUrlList}
          >
            Shorten show
          </button>
        </div>
        <ul className="list-group">
          {shortlinks.map((val, key) => {
            return (
              <li
                key={key}
                className="list-group-item d-flex justify-content-between align-items-center"
              >
                {val.fullurl} {val.shortcode}
                <button className="btn btn-outline-primary">Copy</button>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
}

export default ConvertUrl;
