import React, { useState } from "react";
import "./ConvertUrl.css";
import Axios from "axios";
import validator from "validator";

function ConvertUrl() {
  const [urllist, setUrlList] = useState([]);
  const [fullurl, setFullurl] = useState("");
  const [savesuccess, setSaveSuccess] = useState(false);
  const [savefail, setSaveFail] = useState(false);

  const showSuccess = () => {
    setTimeout(() => {
      setSaveSuccess(false);
    }, 5500);
  };

  const showFail = () => {
    setTimeout(() => {
      setSaveFail(false);
    }, 5500);
  };

  const shortUrlList = () => {
    Axios.get("http://localhost:3001/urlrecord").then((response) => {
      setUrlList([...urllist, response.data]);
      console.log(urllist);
    });

    //setItems([...items, urllist]);
  };

  const createShortUrl = () => {
    if (validator.isURL(fullurl)) {
      setSaveSuccess(true);
      showSuccess();
    } else {
      setSaveFail(true);
      showFail();
    }

    if (validator.isURL(fullurl)) {
      setSaveSuccess(true);
      showSuccess();
    } else {
      setSaveFail(true);
      showFail();
    }

    Axios.post("http://localhost:3001/create", {
      fullurl: fullurl,
    }).then(() => {
      setUrlList([
        ...urllist,
        {
          fullurl: fullurl,
        },
      ]);
    });
  };

  return (
    <div className="container">
      <h1>URL SHORTENER</h1>
      <div className="information">
        <form action="" className="row g-2">
          <div className="col-9">
            <label htmlFor="inputPassword2" className="visually-hidden">
              Password
            </label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Shorten your link"
              onChange={(event) => {
                setFullurl(event.target.value);
              }}
            />
          </div>
          <div className="col-3">
            <button
              className="btn btn-primary btn-lg mb-3"
              onClick={createShortUrl}
            >
              Shorten
            </button>
          </div>
        </form>
        {savesuccess && (
          <div className="alert alert-success" role="alert">
            Is Valid URL
          </div>
        )}
        {savefail && (
          <div className="alert alert-danger" role="alert">
            Is Not Valid URL
          </div>
        )}
        <div className="showurl">
          <button
            className="btn btn-primary btn-lg mb-3"
            onClick={shortUrlList}
          >
            Shorten show
          </button>
        </div>
        <ul className="list-group">
          {urllist.map((val, key) => {
            return (
              <li
                key={key}
                className="list-group-item d-flex justify-content-between align-items-center"
              >
                {val.fullurl} {val.shortcode}
                <button className="btn btn-outline-primary">Copy</button>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
}

export default ConvertUrl;
