import React, { useState } from "react";
import "./ConvertUrl.css";

function ConvertUrl() {
  //const [urllist, setUrlList] = useState("Test Url list");
  const [items, setItems] = useState([]);

  const shortUrlList = () => {
    setItems([
      ...items,
      {
        id: items.length,
        name: "shorturl",
      },
    ]);
  };

  return (
    <div className="container">
      <h1>URL SHORTENER</h1>
      <div className="information">
        <form action="" className="row g-2">
          <div className="col-9">
            <label htmlFor="inputPassword2" className="visually-hidden">
              Password
            </label>
            <input
              type="text"
              className="form-control form-control-lg"
              id="inputPassword2"
              placeholder="Password"
            />
          </div>
          <div className="col-3">
            <button className="btn btn-primary btn-lg mb-3">Shorten</button>
          </div>
        </form>
        <div className="showurl">
          <button
            className="btn btn-primary btn-lg mb-3"
            onClick={shortUrlList}
          >
            Shorten show
          </button>
        </div>
        <ul class="list-group">
          {items.map((item) => (
            <li
              key={item.id}
              class="list-group-item d-flex justify-content-between align-items-center"
            >
              {item.name}
              <button className="btn btn-primary">Copy</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ConvertUrl;
